(function(){const t=window.location.href,n=[{type:"manga",regex:/manga\/([^\/]+)\/chapter-(\d+)/},{type:"anime",regex:/anime\/([^\/]+)\/episode-(\d+)/},{type:"movie",regex:/movie\/([^\/]+)\/watch/},{type:"manhua",regex:/manhua\/([^\/]+)\/chapter-(\d+)/},{type:"manhwa",regex:/manhwa\/([^\/]+)\/chapter-(\d+)/}];for(const a of n){const e=t.match(a.regex);if(e){const o=decodeURIComponent(e[1]),r=e[2]??"watched";chrome.runtime.sendMessage({type:"save-manga",payload:{title:o,chapter:r,mediaType:a.type}});break}}})();
