document.addEventListener("DOMContentLoaded",()=>{chrome.runtime.sendMessage({type:"get-manga-history"},t=>{const e=document.getElementById("manga-table-body");if(e.innerHTML="",!t||!t.success){e.innerHTML="<tr><td colspan='2'>Error loading history</td></tr>";return}for(const d of t.history){const n=document.createElement("tr"),o=document.createElement("td");o.textContent=d.title,n.appendChild(o);const r=document.createElement("td");r.textContent=d.chapter,n.appendChild(r),e.appendChild(n)}})});
